sections.css:
/**
 * 1. Default bottom-margin of 1.5 times the font-size
 * 2. Using single colon so it works in IE8 as well
 * 3. No bottom-margin for all the last-childs so parents can have bottom padding
 */

fieldset {
  margin-bottom: 1.5em; /* 1 */

  &:last-child { /* 2 */
    margin-bottom: 0; /* 3 */
  }
}

/**
 * 1. Responsive images
 * 2. Eliminating line-height bug when used alone in container
 */

img {
  max-width: 100%; /* 1 */
  height: auto;

  &:only-child {
    vertical-align: middle; /* 2 */
  }
}

legend {
  display: block;
  margin-top: 0;
  margin-bottom: $spacing;
  border-bottom: 1px solid darker($form-background);
  padding-top: $spacing;
  padding-bottom: 4px;
  font-size: $font-size--large;
  line-height: $line-height--large;
  font-weight: $regular;

  /* No margin top for the first legend */
  &:first-child {
    margin-top: 0;
  }

  /* No margin bottom for the last legend */
  &:last-child {
    margin-bottom: 0;
  }
}

/**
 * 1. Remove default top-margin
 * 2. Default bottom-margin of 1.5 times the font-size
 * 3. Using single colon so it works in IE8 as well
 * 4. No bottom-margin for all the last-childs so parents can have bottom padding
 */

p {
  margin-top: 0; /* 1 */
  margin-bottom: 1.5em; /* 2 */

  &:last-child { /* 3 */
    margin-bottom: 0; /* 4 */
  }
}

/**
 * 1. Reset default <ul> styling
 */

.k-navigation {
  @include reset($padding: false, $background: false); /* 1 */
  @include border(bottom, $navigation-border);
  padding: ($navigation-spacing * 2) 0;
  background: $navigation-background;


  /* Items */

  li {
    display: block;
    line-height: $line-height;
  }


  /**
   * Links
   *
   * 1. Make item fully clickable
   * 2. removing underlines
   */

  a {
    display: block; /* 1 */
    padding-top: $navigation-spacing;
    padding-bottom: $navigation-spacing;
    padding-right: round($navigation-spacing * 2);
    padding-left: round($navigation-spacing * 3);
    font-size: $navigation-font-size;
    color: $navigation-color;
    text-decoration: none; // 2

    @include rtl {
      padding-right: round($navigation-spacing * 3);
      padding-left: round($navigation-spacing * 2);
    }


    /* Hover & focus state */

    &:hover,
    &:focus {
      background: $list-hover-background;
      color: $list-hover-color;
      text-decoration: none; /* 2 */
    }


    /**
     * Faux border
     *
     * Adding a border to the left, using "after" psuedo since "before" is used for the icon
     */

    @include psuedo(after) {
      left: 0;
      top: 0;
      width: $navigation-border-width;
      height: 100%;

      @include rtl {
        left: auto;
        right: 0;
      }
    }


    /* Hover & focus states */

    &:hover,
    &:focus {
      background: $navigation-hover-background;

      &:after {
        background: $navigation-hover-border;
      }
    }
  }


  /* Active link */

  .active {
    a {
      background: $navigation-active-background;
      color: $navigation-active-color;
      font-weight: $bold;

      &:after {
        top: 12%;
        height: 76%;
        background: $navigation-active-border;
      }

      &:hover {
        background: transparent;
        cursor: default;
      }
    }
  }
}

/* Parent element
   ========================================================================== */

/**
 * 1. Scroll when items don't fit parent anymore
 */

.k-breadcrumb {
  @include border(bottom, $breadcrumb-border);
  overflow: auto; /* 1 */
  background: $breadcrumb-background;
  color: $breadcrumb-color;
  min-width: 100%;

  @include resizing {
    overflow: hidden;
  }
}


/* When flexbox is in use
   ========================================================================== */

@include flexbox {
  .k-breadcrumb {
    flex: 0 0 auto;
  }
}


/* Breadcrumb contents
   ========================================================================== */

.k-breadcrumb {

  /**
   * Links
   *
   * 1. Full background hover
   */

  a {
    color: $breadcrumb-color;
    cursor: pointer;

    &:focus,
    &:hover {
      background: $breadcrumb-background-hover; /* 1 */
    }
  }


  /**
   * List
   *
   * 1. Reset unordered list styling
   */

  ul {
    @include reset; /* 1 */


    /* Flexing items to get overflow ellipsis */

    @include flexbox {
      display: flex;
    }

  }


  /**
   * Styling list items
   *
   * 1. Set z-index to 0 so the :after psuedo element will be on top
   * 2. Make room for the psuedo element separator
   * 3. Make each item at least one breadcrumb size wide
   * 4. Hide by default so we can show only a few on mobile
   * 5. Hide overflowing content
   * 6. Proper spacing for non-flexbox use
   */

  li {
    position: relative; /* 1 */
    z-index: 0; /* 1 */
    padding-left: $breadcrumb-divider-size; /* 2 */
    min-width: $breadcrumb-size; /* 3 */
    display: none; /* 4 */
    overflow: hidden; /* 5 */
    vertical-align: middle; /* 6 */

    @include rtl {
      padding-left: 0;
      padding-right: $breadcrumb-divider-size;
    }


    /* Display on larger screens */

    @include breakpoint($breadcrumb-query) {
      display: inline-block;
    }


    /* Flexing items to get overflow ellipsis */

    @include flexbox($horizontal:$breadcrumb-query) {
      display: block;
      flex: 0 1 auto;
    }


    /* List item separator */

    @include psuedo(before, $content: false) {
      top: 0;
      bottom: 0;
      left: 0;
      width: $breadcrumb-divider-size;
      padding-top: $breadcrumb-size / 4;
      padding-bottom: $breadcrumb-size / 4;
      text-align: center;
      line-height: $breadcrumb-size / 2;
      content: $breadcrumb-divider-content;

      @include rtl {
        left: auto;
        right: 0;
      }
    }


    /* No separator on first item */

    &:first-child {
      padding-left: 0;

      @include rtl {
        padding-right: 0;
      }

      &:before {
        display: none;
      }
    }


    /**
     * Hover
     *
     * 1. Reset flexbox so the whole item is visible
     */

    &:hover {
      overflow: visible;

      @include flexbox {
        flex: 0 0 auto; /* 1 */
      }
    }
  }


  /**
   * Home and last elements
   *
   * 1. Always display these items
   * 2. No flexing for these items so the are readable
   */

  .k-breadcrumb__active,
  .k-breadcrumb__home {
    display: inline-block; /* 1 */
    overflow: visible; /* 1 */

    @include flexbox($horizontal:$breadcrumb-query) {
      flex: 0 0 auto; /* 2 */
    }
  }



  /**
   * Root element
   *
   * 1. Hide root text by default
   * 2. Show and position it when root is the active item to show more then just a home icon
   */

  .k-breadcrumb_root-text {
    @extend .visually-hidden; /* 1 */
  }

  .k-breadcrumb__home.k-breadcrumb__last {
    .k-breadcrumb_root-text {
      @extend .visually-shown; /* 2 */
      padding-left: $spacing-tiny; /* 2 */
      font-family: "Arial", sans-serif;

      @include rtl {
        padding-left: 0;
        padding-right: $spacing-tiny;
      }
    }
  }



  /**
   * One but last item
   *
   * Show this item on mobile to get a sense of where you are
   *
   * 1. Set 'missing' content
   * 2. Reset to default content on wider screens
   */

  li:nth-last-child(2) {
    display: inline-block;

    &:before {
      content: $breadcrumb-missing-content; /* 1 */

      @include breakpoint($breadcrumb-query) {
        content: $breadcrumb-divider-content; /* 2 */
      }
    }
  }


  /* Active item */

  .k-breadcrumb__last {
    .k-breadcrumb__item {
      font-weight: $bold;
    }
  }

}


/* Single breadcrumb items */

.k-breadcrumb__item {
  position: relative;
  z-index: 2;
  display: block;
  padding: round($breadcrumb-size / 4);
  min-width: $breadcrumb-size;
  min-height: $breadcrumb-size;
  overflow: hidden;
  text-align: center;
  line-height: floor($breadcrumb-size / 2);
  text-decoration: none;
  white-space: nowrap;
  text-overflow: ellipsis;
}

.k-scopebar {
  background: $scopebar-background;
  color: $scopebar-color;
  min-width: 100%;

  @include breakpoint($delta) {
    display: table;
    width: 100%;
  }


  /**
   * When flexbox is in use
   */

  @include flexbox {
    flex: 0 0 auto;
  }

  @include flexbox($horizontal: $delta) {
    display: flex;
    width: auto;
    flex: 0 0 auto;
  }


  /**
   * Breadcrumbs in the scopebar
   *
   * When the breadcrumbs are in the scopebar the breadcrumbs should be flexing
   */

  .k-breadcrumb {
    flex: 1 1 auto;
  }
}



/**
 * Items
 */

.k-scopebar__item {
  @include border(bottom, $scopebar-border);
  vertical-align: middle;


  /**
   * When flexbox is in use
   */

  @include flexbox($horizontal: $delta) {
    display: block;
    flex: 0 0 auto;


    /* Search item */

    &.k-scopebar__item--search {
      display: block;
      flex: 0 0 auto;
    }
  }
}



/**
 * Fluid item
 *
 * Put at least one fluid item in every scopebar for flexbox to work correctly
 */

.k-scopebar__item--fluid {
  @include clearfix;

  @include breakpoint($delta) {
    display: table-cell;
    width: 99%;
  }

  @include flexbox($horizontal: $delta) {
    flex: 1 1 auto;
    width: auto;
  }
}




/**
 * Filters
 *
 * 1. Clearfix so we can float individual filters
 * 2. For positioning dropdowns on smaller screens
 */

.k-scopebar__filters {
  @include clearfix; /* 1 */
  @include border(left,$scopebar-border);
  position: relative; /* 2 */
  z-index: 2; /* 2 */
}


/* Individual filter */

.k-scopebar__item--filter {
  display: block;
  float: left;
  width: auto;
  line-height: $scopebar-size;
  @include border(right, #ccc);
  @include border(bottom, #ccc);
  margin-bottom: -1px;
  height: $scopebar-size + 1px;
  font-size: $scopebar-filter-font-size;

  @include breakpoint($beta) {
    position: relative;
  }

  @include rtl {
    border-right: none;
    @include border(left, #ccc);
    float: right;
  }
}


/* Filter title */

.k-scopebar__item--filter__title,
.k-scopebar__item--filter__icon {
  display: inline-block;
  vertical-align: middle;
}

.k-scopebar__item--filter__icon {
  margin-left: 2px;

  @include rtl {
    margin-left: 0;
    margin-right: 2px;
  }

  @include breakpoint($beta) {
    margin-left: 4px;

    @include rtl {
      margin-left: 0;
      margin-right: 4px;
    }
  }
}


/* Scopebar label showing how many active filters you have below a filter button */

.k-scopebar__item__label {
  position: absolute;
  z-index: 2;
  top: 2px;
  right: 2px;
  min-width: 15px;
  height: 15px;
  border-radius: 50%;
  background: #d35400;
  color: white;
  font-size: 10px;
  line-height: 15px;
  text-align: center;
  font-weight: $bold;
  box-shadow: inset 0 0 2px rgba(0,0,0,.1);
  pointer-events: none;

  @include rtl {
    right: auto;
    left: 2px;
  }

  @include breakpoint($beta) {
    top: 6px;
    right: 4px;

    @include rtl {
      right: auto;
      left: 4px;
    }
  }

  &--numberless {
    &:after {
      display: block;
      content: " ";
      width: 5px;
      height: 5px;
      background: white;
      position: absolute;
      top: 5px;
      left: 5px;
      border-radius: 50%;
      box-shadow: 1px 1px 2px rgba(0,0,0,.2);
    }
  }
}



/**
 * Search
 *
 * 1. Hide by default (when JS is enabled) so we can toggle it open
 * 2. Display as table cell on medium sized screens (!important so it displays even if it's hidden by JS)
 * 3. Display as block for flexbox on larger screens
 * 5. Search area items
 */

.k-scopebar__item--search {
  position: relative;

  @include breakpoint($delta) {
    @include allwidth(220px);
  }

  @include breakpoint($echo) {
    @include allwidth(280px);
  }

  @include js-enabled {
    display: none; /* 1 */

    @include breakpoint($delta) {
      display: table-cell!important; /* 2 */
    }
  }


  /* 5 */

  .k-search__container {
    width: 100%;
    position: relative;
  }

  .k-search__container--has-search-button .k-search__field {
    padding-left: 28px;

    @include rtl {
      padding-left: 0;
      padding-right: 28px;
    }
  }

  .k-search__container--has-empty-button .k-search__field {
    padding-right: 18px;

    @include rtl {
      padding-right: 0;
      padding-left: 28px;
    }
  }

  .k-search__container--has-both-buttons .k-search__field {
    padding-left: 28px;
    padding-right: 18px;

    @include rtl {
      padding-left: 0;
      padding-right: 28px;
    }
  }

  label {
    @extend .visually-hidden;
  }

  .k-search__field {
    @include reset;
    margin: 0;
    display: block;
    width: 100%;
    height: $scopebar-size;
    border-radius: 0;
    padding: $spacing-tiny;
    padding-right: $scopebar-size;
    background: $scopebar-search-background;
    color: $scopebar-search-color;
    box-shadow: none;
    font-size: 12px;
    -webkit-appearance: none;

    @include rtl {
      padding-right: 0;
      padding-left: $scopebar-size;
    }

    @include breakpoint($delta) {
      @include border(left, $scopebar-border);
      @include border(bottom, $scopebar-border);
      height: $scopebar-size + 1px;
      margin-bottom: -1px;

      @include rtl {
        border-left: none;
        @include border(right, $scopebar-border);
      }
    }
  }

  .k-search__button-search {
    @include reset;
    position: absolute;
    top: 50%;
    left: .5em;
    margin-top: -.75em;

    @include rtl {
      left: auto;
      right: .5em;
    }
  }

  .k-search__button-empty {
    border: none;
    margin: 0;
    padding: 0;
    position: absolute;
    display: none;
    top: 50%;
    right: 0;
    width: 22px;
    height: 22px;
    margin-top: -10px;
    background: transparent;

    @include rtl {
      right: auto;
      left: 0;
    }

    &.is-visible {
      display: inline-block;
    }

    span {
      margin: 3px;
      -webkit-box-sizing: border-box;
      -moz-box-sizing: border-box;
      box-sizing: border-box;
      width: 16px;
      height: 16px;
      display: inline-block;
      line-height: 16px;
      text-align: center;
      border-radius: 100%;
      color: #777;
      text-decoration: none;
      font-weight: $bold;
      font-family: Arial, helvetica, sans-serif;
      font-size: 10px;
    }

    &:hover {
      cursor: pointer;

      span {
        background: #ea9393;
        border: 1px solid #ce4c4c;
        color: #fff;
        text-shadow: 1px 1px 1px #ce4c4c;
        @include box-shadow(inset 1px 2px 1px lighten(#ce4c4c, 10%));
      }
    }
  }

  // The active search label
  .k-scopebar__item__label--numberless {
    position: absolute;
    right: auto;
    left: 13px;
    top: 5px;

    @include rtl {
      left: auto;
      right: 13px;
    }
  }
}



/**
 * Search toggle button
 *
 * 1. Hide when search is visible
 */

.k-scopebar-icon-button {
  position: relative;
  display: none;
  width: $scopebar-size;
  height: 0;
  margin: 0;
  border: none;
  padding: 0;
  padding-top: $scopebar-size;
  background: transparent;

  [class^="k-icon"] {
    position: absolute;
    top: 50%;
    left: 50%;
    font-size: 16px;
    vertical-align: middle;
    margin: -8px 0 0 -8px;

    @include rtl {
      left: auto;
      right: 50%;
      margin: -8px -8px 0 0;
    }
  }

  @include js-enabled {
    display: block;
  }
}


.k-scopebar__filter-toggle-wrapper,
.k-scopebar__search-toggle-wrapper {
  width: $scopebar-size;
  max-width: $scopebar-size;
  height: $scopebar-size;
  white-space: nowrap;
}

.k-scopebar__filter-toggle-wrapper {
  position: relative;

  .k-scopebar__item__label {
    @include breakpoint($beta) {
      display: none;
    }
  }
}

.k-scopebar__filters-wrapper {
  display: table;
  width: 100%;
  height: auto;
}

.k-scopebar__filter-toggle-wrapper,
.k-scopebar__filters-content,
.k-scopebar__search-toggle-wrapper {
  display: table-cell;
  vertical-align: top;
}

.k-scopebar__search-toggle-wrapper {
  @include breakpoint($delta) {
    display: none;
  }
}

.k-scopebar__filters-content {
  min-height: $scopebar-size;
  min-width: $scopebar-size;
  width: 99%;
}

.k-scopebar__filters {
  @include js-enabled {
    display: none;

    @include breakpoint($beta) {
      display: block !important;
    }
  }
}


.k-toggle-filters {
  @include js-enabled {
    display: block;

    @include breakpoint($beta) {
      display: none;
    }
  }
}

.k-toggle-filters-icon {
  @include js-enabled {
    display: none;

    @include breakpoint($beta) {
      display: block;
    }
  }
}

.k-toggle-search {
  @include js-enabled {
    display: block;
    float: right;

    @include rtl {
      float: left;
    }
  }
}



/**
 * Right floating button group
 */

.k-scopebar__group--right {
  float: right;

  @include rtl {
    float: left;
  }
}

.k-scopebar__group--right__item {
  float: left;
  height: $scopebar-size;

  @include rtl {
    float: right;
  }
}

.k-table-container {
  @include flexbox {
    display: flex;
    flex: 1 1 auto;
    flex-direction: column;
    overflow: auto;
  }

  @include resizing {
    overflow: hidden;
  }

  /* Table container */

  .k-table {
    overflow: auto;
    background: $table-background;
    min-height: 170px;

    @include flexbox {
      display: flex;
      flex: 0 1 auto;
      background: $table-cell-background-even;
    }
  }

  table {
    width: 100%;
    text-align: left;
    border-collapse: separate;

    @include rtl {
      text-align: right;
    }

    @include flexbox {
      flex: 1 1 auto;
    }
  }


  /**
   * Cells
   *
   * 1. Position checkbox
   * 2. Remove default margin and set negative top margin to visually align vertically
   */

  th,
  td {
    position: relative;
    height: $table-cell-size;
    vertical-align: middle;
    text-align: left;
    @include transition;

    input {
      display: inline-block; /* 1 */
      vertical-align: middle; /* 1 */
      margin: -1px 0 0 0; /* 2 */
    }
  }

  @include rtl {
    th,
    td {
      text-align: right;
    }
  }


  /**
   * Head
   *
   * 1. Don't set a vertical padding on table headers. Set padding on table heading children
   *    We do this so <a> elements are fully clickable for sorting
   * 2. Don't use long header titles since they won't be wrapped
   * 3. Whole <th> should be clickable
   * 4. Set padding here
   */

  th {
    @include border(bottom, $table-header-border);
    z-index: 1;
    padding: 0 $table-cell-spacing-horizontal; /* 1 */
    white-space: nowrap; /* 2 */
    background: $table-header-background;

    a {
      display: block; /* 3 */
      padding: $table-cell-spacing-vertical $table-cell-spacing-horizontal; /* 4 */
      margin-left: -$table-cell-spacing-horizontal;
      margin-right: -$table-cell-spacing-horizontal;
      line-height: $table-cell-size - ($table-cell-spacing-vertical * 2);
      @include transition;

      &:focus,
      &:hover {
        background: $table-header-background-hover;
      }
    }
  }


  /**
   * Body
   *
   * 1. Small extra info below title display:block to display below title
   */

  td {
    @include border(bottom, $table-cell-border);
    padding: $table-cell-spacing-vertical $table-cell-spacing-horizontal;
    background: $table-cell-background-even;
    line-height: 18px;
  }

  tr:last-child {
    td {
      border-bottom: none;
    }
  }


  tr:nth-child(odd) {
    td {
      background: $table-cell-background-odd;
    }
  }


  /* Hover background */
  tr,
  tr:nth-child(n) {
    &:focus,
    &:hover {
      td {
        background: $table-cell-background-even-hover;
      }
    }
  }

  small {
    display: block; /* 1 */
    font-size: 11px;
    line-height: 11px;
    max-width: 100%;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
  }

  a {
    text-decoration: none;

    &:hover,
    &:focus {
      text-decoration: underline;
    }
  }

  a {
    @include transition;
  }

  [class^="koowa_icon"] {
    vertical-align: baseline;
    font-size: 16px;
    line-height: 23px;
  }



  /**
   * Table classes
   *
   * Classes to add to table cells
   */

  .k-table-data--right {
    text-align: right;

    @include rtl {
      text-align: left;
    }
  }

  .k-table-data--center {
    text-align: center;
  }

  .k-table-data--nowrap {
    white-space: nowrap;
  }

  .k-table-data--wrap {
    word-break: break-all;
  }

  .k-table-data--toggle {
    white-space: nowrap;
    padding-left: 0;
    padding-right: 0;
  }

  .k-table-data--ellipsis {
    max-width: 0;
    overflow: hidden;

    .k-ellipsis-item {
      vertical-align: middle;
    }
  }



  /**
   * Table levels
   *
   * Indented levels for a clearer overview
   */

  .k-table__item-level {
    white-space: nowrap;

    .k-table__item-cell-ellipsis {
      display: table;
      width: 100%;

      .k-table__item-cell-visual,
      .k-table__item-cell-text {
        display: table-cell;
        vertical-align: middle;
        width: 1%;
      }

      .k-table__item-cell-text {
        display: table-cell;
        width: 99%;
        max-width: 0;
        overflow: hidden;
        text-overflow: ellipsis;
        white-space: nowrap;
      }
    }

    &:before {
      content: " ";
      display: block;
      width: 6px;
      height: 6px;
      border-left: 1px solid #bbb;
      border-bottom: 1px solid #bbb;
      position: absolute;
      top: 50%;
      margin-top: -.25em;

      @include rtl {
        border-left: none;
        border-right: 1px solid #bbb;
      }
    }
  }

  @for $i from 1 through 15 {
    .k-table__item-level#{$i} {
      padding-left: 24px * ($i - 1);

      @include rtl {
        padding-left: 0;
        padding-right: 24px * ($i - 1);
      }

      &:before {
        left: (24px * ($i - 1)) - 18px;

        @include rtl {
          left: auto;
          right: (24px * ($i - 1)) - 18px;
        }
      }
    }
  }

  .k-table__item-level__title {
    padding-left: 5px;

    @include rtl {
      padding-left: 0;
      padding-right: 5px;
    }
  }

  .k-table__item-level1:before {
    display: none;
  }



  /**
   * Table elements
   */

  .k-table-button {
    appearance: none;
    background: white;
    border: 1px solid #ccc;
    border-radius: 2px;
    font-size: 12px;
    padding: 0;
    color: #555;
    display: inline-block;
    vertical-align: middle;
    width: 18px;
    height: 18px;
    line-height: 16px;
    overflow: hidden;
    text-align: center;
    transition: all 0.2s ease-in-out;
    cursor: pointer;
    font-weight: bold;
    margin-right: 6px;

    &:focus,
    &:hover {
      color: $color-error;
      border-color: #aaa;
      background: #eee;
    }

    & + a {
      display: inline-block;
      vertical-align: middle;
    }
  }


  /**
   * Selected
   *
   * When rows are selected they are highlighted
   *
   * 1. Coloring Links and extra elements that don't color automatically with it's parent
   */

  tr.active,
  tr.selected {
    td {
      background: $table-cell-accent-background;
      color: $table-cell-accent-color;

      a, i, span {
        color: $table-cell-accent-color; /* 1 */
      }

      .k-table-button {
        border-color: white;
        background: transparent;

        &:focus,
        &:hover {
          border-color: white;
          background: white;
        }
      }
    }

    &:hover,
    &:focus {
      td {
        background-color: $table-cell-accent-background-hover;
      }
    }
  }



  /* Sub headings */

  .k-table__sub-header {
    th {
      height: auto;
      font-size: .875em;
      text-transform: uppercase;
    }
  }


  /**
   * Sorting arrows
   *
   * 1. Display table to line them up horizontally while:
   *    - We keep everything vertically centered
   *    - We keep it all on one line but don't have to deal with inline-block spacing trouble
   * 2. Set base line-height to 16px to keep it all vertically centered
   * 3. Set min-width to 16px so icons and placeholder have the same width,
   *    lining all arrows perfectly below each other
   * 4. Aligning items in center
   * 5. Min-width for text larger to fit 3 digits by default. Icons will line up nice to at least 999
   * 6. Set a little padding for when text item gets 4 digits
   */

  .k-table-data--sort {
    display: table; /* 1 */
    line-height: 16px; /* 2 */
  }

  .k-grid-sort {
    display: table-cell; /* 1 */
    min-width: 16px; /* 3 */
    text-align: center; /* 4 */
    vertical-align: middle; /* 4 */
  }

  .k-grid-sort--text {
    min-width: 30px; /* 5 */
    padding: 0 3px; /* 6 */
  }



  .dropdown-toggle {
    &,&:hover,&:focus {
      text-decoration: none;
      cursor: pointer;
    }

    input, span {
      display: inline-block;
      vertical-align: middle;
    }
  }

  .dropdown-menu {
    margin-left: 1px;

    @include rtl {
      margin-left: 0;
      margin-right: 1px;
    }

    a {
      margin: 0;
      padding: 5px;

      &:focus,
      &:hover {
        background: $color-accent;
      }
    }
  }
}


/*
 * Table pagination
 *
 * 1. Faking a bottom border, this shadow will disappear when pagination is positioned to bottom
 */

.k-table-pagination {
  padding: $table-pagination-spacing;
  background: $table-pagination-background;
  @include border(top, #d2d2d2);
  @if $borders != false {
    box-shadow: 0 1px 0 $table-pagination-border; /* 1 */
  }


  /* Flexbox */

  @include flexbox {
      flex: 0 0 auto;
  }


  /**
   * Pagination contents
   *
   * 1. Align vertically
   * 2. Remove standard Bootstrap margin
   * 3. Style <select> to be as large as the pagination
   * 4. Make sure <select> is always as small as possible
   * 5. Separate <select> from pagination
   */

  .limit,
  .pagination {
    display: inline-block; /* 1 */
    vertical-align: middle; /* 1 */
  }

  .pagination {
    margin: 0; /* 2 */
  }

  select {
    height: $table-pagination-height; /* 3 */
    width: auto; /* 4 */
    margin-right: $table-pagination-spacing; /* 5 */

    @include rtl {
      margin-right: auto;
      margin-left: $table-pagination-spacing;
    }
  }

  .k-pagination-pages {
    float: right;
    line-height: 30px;

    @include rtl {
      float: left;
    }
  }
}


/**
 * Footable
 *
 * Responsive table views
 */

.footable.breakpoint {
  .footable-toggle {
    display: inline-block;
    width: $footable-toggle-size;
    height: $footable-toggle-size;
    line-height: ($footable-toggle-size - ($footable-toggle-border-size * 2));
    border: $footable-toggle-border-size solid $footable-toggle-color;
    border-radius: 50%;
    margin-right: $table-cell-spacing-horizontal;
    text-align: center;

    @include rtl {
      margin-right: 0;
      margin-left: $table-cell-spacing-horizontal;
    }

    @include icon-after(chevron-bottom, true) {
      display: inline-block;
      width: $footable-toggle-icon-size;
      height: $footable-toggle-icon-size;
      margin: ((($footable-toggle-size - $footable-toggle-icon-size) / 2) - $footable-toggle-border-size);
      color: $footable-toggle-color;
      font-size: $footable-toggle-icon-size;
      line-height: 1.3em;
      transform: rotate(-90deg);
      @include transition;
    }
  }

  tr.selected {
    .footable-toggle {
      border-color: $footable-toggle-color-active;

      &:after {
        color: $footable-toggle-color-active;
      }
    }
  }

  tr.footable-detail-show {
    .footable-toggle {
      &:after {
        transform: rotate(0deg);
      }
    }
  }

  .footable-row-detail-row {
    margin-bottom: $table-cell-spacing-vertical;

    &:last-child {
      margin-bottom: 0;
    }
  }

  .footable-row-detail-name {
    font-weight: $bold;
  }
}


/* Table item states */

.k-table__item--state {
  cursor: pointer;
  @include transition;

  &:focus,
  &:hover {
    text-decoration: underline;
  }

  &.k-pending {
    color: $color-warning;
  }

  &.k-expired {
    color: $color-neutral;
  }

  &.k-unpublished {
    color: $color-error;
  }

  &.k-published {
    color: $color-success;
  }
}

.k-table__sub-header {
  th {
    background: white;
  }
}

.k-toolbar {
  @include border(bottom, $toolbar-border);
  background: $toolbar-background;
  min-height: $toolbar-height;


  /* Faking flexbox for older browsers */

  @include breakpoint($desktop) {
    width: 100%;
    display: table;
  }


  /* When flexbox is in use */

  @include flexbox {
    flex: 0 0 auto;
  }
}


/* Toolbar contents */

.k-toolbar {
  button,
  .btn {
    margin: 10px 0 10px 10px;

    /**
     * Make buttons square
     *
     * Only make them square if media queries are supported
     * By default we just want them as buttons
     */

    @include breakpoint(0, range, $charlie) {
      overflow: hidden;
      width: 32px;
      height: 32px;
      padding: 7px 6px;
      text-align: center;
    }
  }

  @include rtl {
    button,
    .btn {
      margin: 10px 10px 10px 0;
    }
  }


  /**
   * Alternative button colors
   *
   * If the button colors don't fit the toolbar you can have alternative colors
   */

  @if $toolbar-btn-alt == true {
    .btn-success {
      border-color: $toolbar-btn-success-border;
      background: $toolbar-btn-success-background;
      color: $toolbar-btn-success-color;

      &:hover,
      &:focus {
        border-color: $toolbar-btn-success-hover-border;
        background: $toolbar-btn-success-hover-background;
        color: $toolbar-btn-success-hover-color;
      }
    }
  }


  /**
   * Position the icons
   *
   * Only position them if media queries are supported
   * By default we just want them aligned normally
   */

  i {
    @include breakpoint(0, range, $charlie) {
      display: block;
      margin: 0;
      width: $font-size-large;
      height: $font-size-large;
      font-size: $font-size-large;
      line-height: $font-size-large;
    }

    display: inline-block;
    margin: 0 .25em 0 .1em;
  }
}


/* Text on the toolbar buttons */

.k-toolbar__text {
  @include breakpoint(0, range, $charlie) {
    position: relative;
    z-index: -1;
    visibility: hidden;
  }
}

section.css:
/**
 * 1. Default bottom-margin of 1.5 times the font-size
 * 2. Using single colon so it works in IE8 as well
 * 3. No bottom-margin for all the last-childs so parents can have bottom padding
 */

fieldset {
  margin-bottom: 1.5em; /* 1 */

  &:last-child { /* 2 */
    margin-bottom: 0; /* 3 */
  }
}

/**
 * 1. Responsive images
 * 2. Eliminating line-height bug when used alone in container
 */

img {
  max-width: 100%; /* 1 */
  height: auto;

  &:only-child {
    vertical-align: middle; /* 2 */
  }
}

legend {
  display: block;
  margin-top: 0;
  margin-bottom: $spacing;
  border-bottom: 1px solid darker($form-background);
  padding-top: $spacing;
  padding-bottom: 4px;
  font-size: $font-size--large;
  line-height: $line-height--large;
  font-weight: $regular;

  /* No margin top for the first legend */
  &:first-child {
    margin-top: 0;
  }

  /* No margin bottom for the last legend */
  &:last-child {
    margin-bottom: 0;
  }
}

/**
 * 1. Remove default top-margin
 * 2. Default bottom-margin of 1.5 times the font-size
 * 3. Using single colon so it works in IE8 as well
 * 4. No bottom-margin for all the last-childs so parents can have bottom padding
 */

p {
  margin-top: 0; /* 1 */
  margin-bottom: 1.5em; /* 2 */

  &:last-child { /* 3 */
    margin-bottom: 0; /* 4 */
  }
}

/**
 * 1. Reset default <ul> styling
 */

.k-navigation {
  @include reset($padding: false, $background: false); /* 1 */
  @include border(bottom, $navigation-border);
  padding: ($navigation-spacing * 2) 0;
  background: $navigation-background;


  /* Items */

  li {
    display: block;
    line-height: $line-height;
  }


  /**
   * Links
   *
   * 1. Make item fully clickable
   * 2. removing underlines
   */

  a {
    display: block; /* 1 */
    padding-top: $navigation-spacing;
    padding-bottom: $navigation-spacing;
    padding-right: round($navigation-spacing * 2);
    padding-left: round($navigation-spacing * 3);
    font-size: $navigation-font-size;
    color: $navigation-color;
    text-decoration: none; // 2

    @include rtl {
      padding-right: round($navigation-spacing * 3);
      padding-left: round($navigation-spacing * 2);
    }


    /* Hover & focus state */

    &:hover,
    &:focus {
      background: $list-hover-background;
      color: $list-hover-color;
      text-decoration: none; /* 2 */
    }


    /**
     * Faux border
     *
     * Adding a border to the left, using "after" psuedo since "before" is used for the icon
     */

    @include psuedo(after) {
      left: 0;
      top: 0;
      width: $navigation-border-width;
      height: 100%;

      @include rtl {
        left: auto;
        right: 0;
      }
    }


    /* Hover & focus states */

    &:hover,
    &:focus {
      background: $navigation-hover-background;

      &:after {
        background: $navigation-hover-border;
      }
    }
  }


  /* Active link */

  .active {
    a {
      background: $navigation-active-background;
      color: $navigation-active-color;
      font-weight: $bold;

      &:after {
        top: 12%;
        height: 76%;
        background: $navigation-active-border;
      }

      &:hover {
        background: transparent;
        cursor: default;
      }
    }
  }
}

/* Parent element
   ========================================================================== */

/**
 * 1. Scroll when items don't fit parent anymore
 */

.k-breadcrumb {
  @include border(bottom, $breadcrumb-border);
  overflow: auto; /* 1 */
  background: $breadcrumb-background;
  color: $breadcrumb-color;
  min-width: 100%;

  @include resizing {
    overflow: hidden;
  }
}


/* When flexbox is in use
   ========================================================================== */

@include flexbox {
  .k-breadcrumb {
    flex: 0 0 auto;
  }
}


/* Breadcrumb contents
   ========================================================================== */

.k-breadcrumb {

  /**
   * Links
   *
   * 1. Full background hover
   */

  a {
    color: $breadcrumb-color;
    cursor: pointer;

    &:focus,
    &:hover {
      background: $breadcrumb-background-hover; /* 1 */
    }
  }


  /**
   * List
   *
   * 1. Reset unordered list styling
   */

  ul {
    @include reset; /* 1 */


    /* Flexing items to get overflow ellipsis */

    @include flexbox {
      display: flex;
    }

  }


  /**
   * Styling list items
   *
   * 1. Set z-index to 0 so the :after psuedo element will be on top
   * 2. Make room for the psuedo element separator
   * 3. Make each item at least one breadcrumb size wide
   * 4. Hide by default so we can show only a few on mobile
   * 5. Hide overflowing content
   * 6. Proper spacing for non-flexbox use
   */

  li {
    position: relative; /* 1 */
    z-index: 0; /* 1 */
    padding-left: $breadcrumb-divider-size; /* 2 */
    min-width: $breadcrumb-size; /* 3 */
    display: none; /* 4 */
    overflow: hidden; /* 5 */
    vertical-align: middle; /* 6 */

    @include rtl {
      padding-left: 0;
      padding-right: $breadcrumb-divider-size;
    }


    /* Display on larger screens */

    @include breakpoint($breadcrumb-query) {
      display: inline-block;
    }


    /* Flexing items to get overflow ellipsis */

    @include flexbox($horizontal:$breadcrumb-query) {
      display: block;
      flex: 0 1 auto;
    }


    /* List item separator */

    @include psuedo(before, $content: false) {
      top: 0;
      bottom: 0;
      left: 0;
      width: $breadcrumb-divider-size;
      padding-top: $breadcrumb-size / 4;
      padding-bottom: $breadcrumb-size / 4;
      text-align: center;
      line-height: $breadcrumb-size / 2;
      content: $breadcrumb-divider-content;

      @include rtl {
        left: auto;
        right: 0;
      }
    }


    /* No separator on first item */

    &:first-child {
      padding-left: 0;

      @include rtl {
        padding-right: 0;
      }

      &:before {
        display: none;
      }
    }


    /**
     * Hover
     *
     * 1. Reset flexbox so the whole item is visible
     */

    &:hover {
      overflow: visible;

      @include flexbox {
        flex: 0 0 auto; /* 1 */
      }
    }
  }


  /**
   * Home and last elements
   *
   * 1. Always display these items
   * 2. No flexing for these items so the are readable
   */

  .k-breadcrumb__active,
  .k-breadcrumb__home {
    display: inline-block; /* 1 */
    overflow: visible; /* 1 */

    @include flexbox($horizontal:$breadcrumb-query) {
      flex: 0 0 auto; /* 2 */
    }
  }



  /**
   * Root element
   *
   * 1. Hide root text by default
   * 2. Show and position it when root is the active item to show more then just a home icon
   */

  .k-breadcrumb_root-text {
    @extend .visually-hidden; /* 1 */
  }

  .k-breadcrumb__home.k-breadcrumb__last {
    .k-breadcrumb_root-text {
      @extend .visually-shown; /* 2 */
      padding-left: $spacing-tiny; /* 2 */
      font-family: "Arial", sans-serif;

      @include rtl {
        padding-left: 0;
        padding-right: $spacing-tiny;
      }
    }
  }



  /**
   * One but last item
   *
   * Show this item on mobile to get a sense of where you are
   *
   * 1. Set 'missing' content
   * 2. Reset to default content on wider screens
   */

  li:nth-last-child(2) {
    display: inline-block;

    &:before {
      content: $breadcrumb-missing-content; /* 1 */

      @include breakpoint($breadcrumb-query) {
        content: $breadcrumb-divider-content; /* 2 */
      }
    }
  }


  /* Active item */

  .k-breadcrumb__last {
    .k-breadcrumb__item {
      font-weight: $bold;
    }
  }

}


/* Single breadcrumb items */

.k-breadcrumb__item {
  position: relative;
  z-index: 2;
  display: block;
  padding: round($breadcrumb-size / 4);
  min-width: $breadcrumb-size;
  min-height: $breadcrumb-size;
  overflow: hidden;
  text-align: center;
  line-height: floor($breadcrumb-size / 2);
  text-decoration: none;
  white-space: nowrap;
  text-overflow: ellipsis;
}

.k-scopebar {
  background: $scopebar-background;
  color: $scopebar-color;
  min-width: 100%;

  @include breakpoint($delta) {
    display: table;
    width: 100%;
  }


  /**
   * When flexbox is in use
   */

  @include flexbox {
    flex: 0 0 auto;
  }

  @include flexbox($horizontal: $delta) {
    display: flex;
    width: auto;
    flex: 0 0 auto;
  }


  /**
   * Breadcrumbs in the scopebar
   *
   * When the breadcrumbs are in the scopebar the breadcrumbs should be flexing
   */

  .k-breadcrumb {
    flex: 1 1 auto;
  }
}



/**
 * Items
 */

.k-scopebar__item {
  @include border(bottom, $scopebar-border);
  vertical-align: middle;


  /**
   * When flexbox is in use
   */

  @include flexbox($horizontal: $delta) {
    display: block;
    flex: 0 0 auto;


    /* Search item */

    &.k-scopebar__item--search {
      display: block;
      flex: 0 0 auto;
    }
  }
}



/**
 * Fluid item
 *
 * Put at least one fluid item in every scopebar for flexbox to work correctly
 */

.k-scopebar__item--fluid {
  @include clearfix;

  @include breakpoint($delta) {
    display: table-cell;
    width: 99%;
  }

  @include flexbox($horizontal: $delta) {
    flex: 1 1 auto;
    width: auto;
  }
}




/**
 * Filters
 *
 * 1. Clearfix so we can float individual filters
 * 2. For positioning dropdowns on smaller screens
 */

.k-scopebar__filters {
  @include clearfix; /* 1 */
  @include border(left,$scopebar-border);
  position: relative; /* 2 */
  z-index: 2; /* 2 */
}


/* Individual filter */

.k-scopebar__item--filter {
  display: block;
  float: left;
  width: auto;
  line-height: $scopebar-size;
  @include border(right, #ccc);
  @include border(bottom, #ccc);
  margin-bottom: -1px;
  height: $scopebar-size + 1px;
  font-size: $scopebar-filter-font-size;

  @include breakpoint($beta) {
    position: relative;
  }

  @include rtl {
    border-right: none;
    @include border(left, #ccc);
    float: right;
  }
}


/* Filter title */

.k-scopebar__item--filter__title,
.k-scopebar__item--filter__icon {
  display: inline-block;
  vertical-align: middle;
}

.k-scopebar__item--filter__icon {
  margin-left: 2px;

  @include rtl {
    margin-left: 0;
    margin-right: 2px;
  }

  @include breakpoint($beta) {
    margin-left: 4px;

    @include rtl {
      margin-left: 0;
      margin-right: 4px;
    }
  }
}


/* Scopebar label showing how many active filters you have below a filter button */

.k-scopebar__item__label {
  position: absolute;
  z-index: 2;
  top: 2px;
  right: 2px;
  min-width: 15px;
  height: 15px;
  border-radius: 50%;
  background: #d35400;
  color: white;
  font-size: 10px;
  line-height: 15px;
  text-align: center;
  font-weight: $bold;
  box-shadow: inset 0 0 2px rgba(0,0,0,.1);
  pointer-events: none;

  @include rtl {
    right: auto;
    left: 2px;
  }

  @include breakpoint($beta) {
    top: 6px;
    right: 4px;

    @include rtl {
      right: auto;
      left: 4px;
    }
  }

  &--numberless {
    &:after {
      display: block;
      content: " ";
      width: 5px;
      height: 5px;
      background: white;
      position: absolute;
      top: 5px;
      left: 5px;
      border-radius: 50%;
      box-shadow: 1px 1px 2px rgba(0,0,0,.2);
    }
  }
}



/**
 * Search
 *
 * 1. Hide by default (when JS is enabled) so we can toggle it open
 * 2. Display as table cell on medium sized screens (!important so it displays even if it's hidden by JS)
 * 3. Display as block for flexbox on larger screens
 * 5. Search area items
 */

.k-scopebar__item--search {
  position: relative;

  @include breakpoint($delta) {
    @include allwidth(220px);
  }

  @include breakpoint($echo) {
    @include allwidth(280px);
  }

  @include js-enabled {
    display: none; /* 1 */

    @include breakpoint($delta) {
      display: table-cell!important; /* 2 */
    }
  }


  /* 5 */

  .k-search__container {
    width: 100%;
    position: relative;
  }

  .k-search__container--has-search-button .k-search__field {
    padding-left: 28px;

    @include rtl {
      padding-left: 0;
      padding-right: 28px;
    }
  }

  .k-search__container--has-empty-button .k-search__field {
    padding-right: 18px;

    @include rtl {
      padding-right: 0;
      padding-left: 28px;
    }
  }

  .k-search__container--has-both-buttons .k-search__field {
    padding-left: 28px;
    padding-right: 18px;

    @include rtl {
      padding-left: 0;
      padding-right: 28px;
    }
  }

  label {
    @extend .visually-hidden;
  }

  .k-search__field {
    @include reset;
    margin: 0;
    display: block;
    width: 100%;
    height: $scopebar-size;
    border-radius: 0;
    padding: $spacing-tiny;
    padding-right: $scopebar-size;
    background: $scopebar-search-background;
    color: $scopebar-search-color;
    box-shadow: none;
    font-size: 12px;
    -webkit-appearance: none;

    @include rtl {
      padding-right: 0;
      padding-left: $scopebar-size;
    }

    @include breakpoint($delta) {
      @include border(left, $scopebar-border);
      @include border(bottom, $scopebar-border);
      height: $scopebar-size + 1px;
      margin-bottom: -1px;

      @include rtl {
        border-left: none;
        @include border(right, $scopebar-border);
      }
    }
  }

  .k-search__button-search {
    @include reset;
    position: absolute;
    top: 50%;
    left: .5em;
    margin-top: -.75em;

    @include rtl {
      left: auto;
      right: .5em;
    }
  }

  .k-search__button-empty {
    border: none;
    margin: 0;
    padding: 0;
    position: absolute;
    display: none;
    top: 50%;
    right: 0;
    width: 22px;
    height: 22px;
    margin-top: -10px;
    background: transparent;

    @include rtl {
      right: auto;
      left: 0;
    }

    &.is-visible {
      display: inline-block;
    }

    span {
      margin: 3px;
      -webkit-box-sizing: border-box;
      -moz-box-sizing: border-box;
      box-sizing: border-box;
      width: 16px;
      height: 16px;
      display: inline-block;
      line-height: 16px;
      text-align: center;
      border-radius: 100%;
      color: #777;
      text-decoration: none;
      font-weight: $bold;
      font-family: Arial, helvetica, sans-serif;
      font-size: 10px;
    }

    &:hover {
      cursor: pointer;

      span {
        background: #ea9393;
        border: 1px solid #ce4c4c;
        color: #fff;
        text-shadow: 1px 1px 1px #ce4c4c;
        @include box-shadow(inset 1px 2px 1px lighten(#ce4c4c, 10%));
      }
    }
  }

  // The active search label
  .k-scopebar__item__label--numberless {
    position: absolute;
    right: auto;
    left: 13px;
    top: 5px;

    @include rtl {
      left: auto;
      right: 13px;
    }
  }
}



/**
 * Search toggle button
 *
 * 1. Hide when search is visible
 */

.k-scopebar-icon-button {
  position: relative;
  display: none;
  width: $scopebar-size;
  height: 0;
  margin: 0;
  border: none;
  padding: 0;
  padding-top: $scopebar-size;
  background: transparent;

  [class^="k-icon"] {
    position: absolute;
    top: 50%;
    left: 50%;
    font-size: 16px;
    vertical-align: middle;
    margin: -8px 0 0 -8px;

    @include rtl {
      left: auto;
      right: 50%;
      margin: -8px -8px 0 0;
    }
  }

  @include js-enabled {
    display: block;
  }
}


.k-scopebar__filter-toggle-wrapper,
.k-scopebar__search-toggle-wrapper {
  width: $scopebar-size;
  max-width: $scopebar-size;
  height: $scopebar-size;
  white-space: nowrap;
}

.k-scopebar__filter-toggle-wrapper {
  position: relative;

  .k-scopebar__item__label {
    @include breakpoint($beta) {
      display: none;
    }
  }
}

.k-scopebar__filters-wrapper {
  display: table;
  width: 100%;
  height: auto;
}

.k-scopebar__filter-toggle-wrapper,
.k-scopebar__filters-content,
.k-scopebar__search-toggle-wrapper {
  display: table-cell;
  vertical-align: top;
}

.k-scopebar__search-toggle-wrapper {
  @include breakpoint($delta) {
    display: none;
  }
}

.k-scopebar__filters-content {
  min-height: $scopebar-size;
  min-width: $scopebar-size;
  width: 99%;
}

.k-scopebar__filters {
  @include js-enabled {
    display: none;

    @include breakpoint($beta) {
      display: block !important;
    }
  }
}


.k-toggle-filters {
  @include js-enabled {
    display: block;

    @include breakpoint($beta) {
      display: none;
    }
  }
}

.k-toggle-filters-icon {
  @include js-enabled {
    display: none;

    @include breakpoint($beta) {
      display: block;
    }
  }
}

.k-toggle-search {
  @include js-enabled {
    display: block;
    float: right;

    @include rtl {
      float: left;
    }
  }
}



/**
 * Right floating button group
 */

.k-scopebar__group--right {
  float: right;

  @include rtl {
    float: left;
  }
}

.k-scopebar__group--right__item {
  float: left;
  height: $scopebar-size;

  @include rtl {
    float: right;
  }
}

.k-table-container {
  @include flexbox {
    display: flex;
    flex: 1 1 auto;
    flex-direction: column;
    overflow: auto;
  }

  @include resizing {
    overflow: hidden;
  }

  /* Table container */

  .k-table {
    overflow: auto;
    background: $table-background;
    min-height: 170px;

    @include flexbox {
      display: flex;
      flex: 0 1 auto;
      background: $table-cell-background-even;
    }
  }

  table {
    width: 100%;
    text-align: left;
    border-collapse: separate;

    @include rtl {
      text-align: right;
    }

    @include flexbox {
      flex: 1 1 auto;
    }
  }


  /**
   * Cells
   *
   * 1. Position checkbox
   * 2. Remove default margin and set negative top margin to visually align vertically
   */

  th,
  td {
    position: relative;
    height: $table-cell-size;
    vertical-align: middle;
    text-align: left;
    @include transition;

    input {
      display: inline-block; /* 1 */
      vertical-align: middle; /* 1 */
      margin: -1px 0 0 0; /* 2 */
    }
  }

  @include rtl {
    th,
    td {
      text-align: right;
    }
  }


  /**
   * Head
   *
   * 1. Don't set a vertical padding on table headers. Set padding on table heading children
   *    We do this so <a> elements are fully clickable for sorting
   * 2. Don't use long header titles since they won't be wrapped
   * 3. Whole <th> should be clickable
   * 4. Set padding here
   */

  th {
    @include border(bottom, $table-header-border);
    z-index: 1;
    padding: 0 $table-cell-spacing-horizontal; /* 1 */
    white-space: nowrap; /* 2 */
    background: $table-header-background;

    a {
      display: block; /* 3 */
      padding: $table-cell-spacing-vertical $table-cell-spacing-horizontal; /* 4 */
      margin-left: -$table-cell-spacing-horizontal;
      margin-right: -$table-cell-spacing-horizontal;
      line-height: $table-cell-size - ($table-cell-spacing-vertical * 2);
      @include transition;

      &:focus,
      &:hover {
        background: $table-header-background-hover;
      }
    }
  }


  /**
   * Body
   *
   * 1. Small extra info below title display:block to display below title
   */

  td {
    @include border(bottom, $table-cell-border);
    padding: $table-cell-spacing-vertical $table-cell-spacing-horizontal;
    background: $table-cell-background-even;
    line-height: 18px;
  }

  tr:last-child {
    td {
      border-bottom: none;
    }
  }


  tr:nth-child(odd) {
    td {
      background: $table-cell-background-odd;
    }
  }


  /* Hover background */
  tr,
  tr:nth-child(n) {
    &:focus,
    &:hover {
      td {
        background: $table-cell-background-even-hover;
      }
    }
  }

  small {
    display: block; /* 1 */
    font-size: 11px;
    line-height: 11px;
    max-width: 100%;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
  }

  a {
    text-decoration: none;

    &:hover,
    &:focus {
      text-decoration: underline;
    }
  }

  a {
    @include transition;
  }

  [class^="koowa_icon"] {
    vertical-align: baseline;
    font-size: 16px;
    line-height: 23px;
  }



  /**
   * Table classes
   *
   * Classes to add to table cells
   */

  .k-table-data--right {
    text-align: right;

    @include rtl {
      text-align: left;
    }
  }

  .k-table-data--center {
    text-align: center;
  }

  .k-table-data--nowrap {
    white-space: nowrap;
  }

  .k-table-data--wrap {
    word-break: break-all;
  }

  .k-table-data--toggle {
    white-space: nowrap;
    padding-left: 0;
    padding-right: 0;
  }

  .k-table-data--ellipsis {
    max-width: 0;
    overflow: hidden;

    .k-ellipsis-item {
      vertical-align: middle;
    }
  }



  /**
   * Table levels
   *
   * Indented levels for a clearer overview
   */

  .k-table__item-level {
    white-space: nowrap;

    .k-table__item-cell-ellipsis {
      display: table;
      width: 100%;

      .k-table__item-cell-visual,
      .k-table__item-cell-text {
        display: table-cell;
        vertical-align: middle;
        width: 1%;
      }

      .k-table__item-cell-text {
        display: table-cell;
        width: 99%;
        max-width: 0;
        overflow: hidden;
        text-overflow: ellipsis;
        white-space: nowrap;
      }
    }

    &:before {
      content: " ";
      display: block;
      width: 6px;
      height: 6px;
      border-left: 1px solid #bbb;
      border-bottom: 1px solid #bbb;
      position: absolute;
      top: 50%;
      margin-top: -.25em;

      @include rtl {
        border-left: none;
        border-right: 1px solid #bbb;
      }
    }
  }

  @for $i from 1 through 15 {
    .k-table__item-level#{$i} {
      padding-left: 24px * ($i - 1);

      @include rtl {
        padding-left: 0;
        padding-right: 24px * ($i - 1);
      }

      &:before {
        left: (24px * ($i - 1)) - 18px;

        @include rtl {
          left: auto;
          right: (24px * ($i - 1)) - 18px;
        }
      }
    }
  }

  .k-table__item-level__title {
    padding-left: 5px;

    @include rtl {
      padding-left: 0;
      padding-right: 5px;
    }
  }

  .k-table__item-level1:before {
    display: none;
  }



  /**
   * Table elements
   */

  .k-table-button {
    appearance: none;
    background: white;
    border: 1px solid #ccc;
    border-radius: 2px;
    font-size: 12px;
    padding: 0;
    color: #555;
    display: inline-block;
    vertical-align: middle;
    width: 18px;
    height: 18px;
    line-height: 16px;
    overflow: hidden;
    text-align: center;
    transition: all 0.2s ease-in-out;
    cursor: pointer;
    font-weight: bold;
    margin-right: 6px;

    &:focus,
    &:hover {
      color: $color-error;
      border-color: #aaa;
      background: #eee;
    }

    & + a {
      display: inline-block;
      vertical-align: middle;
    }
  }


  /**
   * Selected
   *
   * When rows are selected they are highlighted
   *
   * 1. Coloring Links and extra elements that don't color automatically with it's parent
   */

  tr.active,
  tr.selected {
    td {
      background: $table-cell-accent-background;
      color: $table-cell-accent-color;

      a, i, span {
        color: $table-cell-accent-color; /* 1 */
      }

      .k-table-button {
        border-color: white;
        background: transparent;

        &:focus,
        &:hover {
          border-color: white;
          background: white;
        }
      }
    }

    &:hover,
    &:focus {
      td {
        background-color: $table-cell-accent-background-hover;
      }
    }
  }



  /* Sub headings */

  .k-table__sub-header {
    th {
      height: auto;
      font-size: .875em;
      text-transform: uppercase;
    }
  }


  /**
   * Sorting arrows
   *
   * 1. Display table to line them up horizontally while:
   *    - We keep everything vertically centered
   *    - We keep it all on one line but don't have to deal with inline-block spacing trouble
   * 2. Set base line-height to 16px to keep it all vertically centered
   * 3. Set min-width to 16px so icons and placeholder have the same width,
   *    lining all arrows perfectly below each other
   * 4. Aligning items in center
   * 5. Min-width for text larger to fit 3 digits by default. Icons will line up nice to at least 999
   * 6. Set a little padding for when text item gets 4 digits
   */

  .k-table-data--sort {
    display: table; /* 1 */
    line-height: 16px; /* 2 */
  }

  .k-grid-sort {
    display: table-cell; /* 1 */
    min-width: 16px; /* 3 */
    text-align: center; /* 4 */
    vertical-align: middle; /* 4 */
  }

  .k-grid-sort--text {
    min-width: 30px; /* 5 */
    padding: 0 3px; /* 6 */
  }



  .dropdown-toggle {
    &,&:hover,&:focus {
      text-decoration: none;
      cursor: pointer;
    }

    input, span {
      display: inline-block;
      vertical-align: middle;
    }
  }

  .dropdown-menu {
    margin-left: 1px;

    @include rtl {
      margin-left: 0;
      margin-right: 1px;
    }

    a {
      margin: 0;
      padding: 5px;

      &:focus,
      &:hover {
        background: $color-accent;
      }
    }
  }
}


/*
 * Table pagination
 *
 * 1. Faking a bottom border, this shadow will disappear when pagination is positioned to bottom
 */

.k-table-pagination {
  padding: $table-pagination-spacing;
  background: $table-pagination-background;
  @include border(top, #d2d2d2);
  @if $borders != false {
    box-shadow: 0 1px 0 $table-pagination-border; /* 1 */
  }


  /* Flexbox */

  @include flexbox {
      flex: 0 0 auto;
  }


  /**
   * Pagination contents
   *
   * 1. Align vertically
   * 2. Remove standard Bootstrap margin
   * 3. Style <select> to be as large as the pagination
   * 4. Make sure <select> is always as small as possible
   * 5. Separate <select> from pagination
   */

  .limit,
  .pagination {
    display: inline-block; /* 1 */
    vertical-align: middle; /* 1 */
  }

  .pagination {
    margin: 0; /* 2 */
  }

  select {
    height: $table-pagination-height; /* 3 */
    width: auto; /* 4 */
    margin-right: $table-pagination-spacing; /* 5 */

    @include rtl {
      margin-right: auto;
      margin-left: $table-pagination-spacing;
    }
  }

  .k-pagination-pages {
    float: right;
    line-height: 30px;

    @include rtl {
      float: left;
    }
  }
}


/**
 * Footable
 *
 * Responsive table views
 */

.footable.breakpoint {
  .footable-toggle {
    display: inline-block;
    width: $footable-toggle-size;
    height: $footable-toggle-size;
    line-height: ($footable-toggle-size - ($footable-toggle-border-size * 2));
    border: $footable-toggle-border-size solid $footable-toggle-color;
    border-radius: 50%;
    margin-right: $table-cell-spacing-horizontal;
    text-align: center;

    @include rtl {
      margin-right: 0;
      margin-left: $table-cell-spacing-horizontal;
    }

    @include icon-after(chevron-bottom, true) {
      display: inline-block;
      width: $footable-toggle-icon-size;
      height: $footable-toggle-icon-size;
      margin: ((($footable-toggle-size - $footable-toggle-icon-size) / 2) - $footable-toggle-border-size);
      color: $footable-toggle-color;
      font-size: $footable-toggle-icon-size;
      line-height: 1.3em;
      transform: rotate(-90deg);
      @include transition;
    }
  }

  tr.selected {
    .footable-toggle {
      border-color: $footable-toggle-color-active;

      &:after {
        color: $footable-toggle-color-active;
      }
    }
  }

  tr.footable-detail-show {
    .footable-toggle {
      &:after {
        transform: rotate(0deg);
      }
    }
  }

  .footable-row-detail-row {
    margin-bottom: $table-cell-spacing-vertical;

    &:last-child {
      margin-bottom: 0;
    }
  }

  .footable-row-detail-name {
    font-weight: $bold;
  }
}


/* Table item states */

.k-table__item--state {
  cursor: pointer;
  @include transition;

  &:focus,
  &:hover {
    text-decoration: underline;
  }

  &.k-pending {
    color: $color-warning;
  }

  &.k-expired {
    color: $color-neutral;
  }

  &.k-unpublished {
    color: $color-error;
  }

  &.k-published {
    color: $color-success;
  }
}

.k-table__sub-header {
  th {
    background: white;
  }
}

.k-toolbar {
  @include border(bottom, $toolbar-border);
  background: $toolbar-background;
  min-height: $toolbar-height;


  /* Faking flexbox for older browsers */

  @include breakpoint($desktop) {
    width: 100%;
    display: table;
  }


  /* When flexbox is in use */

  @include flexbox {
    flex: 0 0 auto;
  }
}


/* Toolbar contents */

.k-toolbar {
  button,
  .btn {
    margin: 10px 0 10px 10px;

    /**
     * Make buttons square
     *
     * Only make them square if media queries are supported
     * By default we just want them as buttons
     */

    @include breakpoint(0, range, $charlie) {
      overflow: hidden;
      width: 32px;
      height: 32px;
      padding: 7px 6px;
      text-align: center;
    }
  }

  @include rtl {
    button,
    .btn {
      margin: 10px 10px 10px 0;
    }
  }


  /**
   * Alternative button colors
   *
   * If the button colors don't fit the toolbar you can have alternative colors
   */

  @if $toolbar-btn-alt == true {
    .btn-success {
      border-color: $toolbar-btn-success-border;
      background: $toolbar-btn-success-background;
      color: $toolbar-btn-success-color;

      &:hover,
      &:focus {
        border-color: $toolbar-btn-success-hover-border;
        background: $toolbar-btn-success-hover-background;
        color: $toolbar-btn-success-hover-color;
      }
    }
  }


  /**
   * Position the icons
   *
   * Only position them if media queries are supported
   * By default we just want them aligned normally
   */

  i {
    @include breakpoint(0, range, $charlie) {
      display: block;
      margin: 0;
      width: $font-size-large;
      height: $font-size-large;
      font-size: $font-size-large;
      line-height: $font-size-large;
    }

    display: inline-block;
    margin: 0 .25em 0 .1em;
  }
}


/* Text on the toolbar buttons */

.k-toolbar__text {
  @include breakpoint(0, range, $charlie) {
    position: relative;
    z-index: -1;
    visibility: hidden;
  }
}
